@Component({
  selector: 'kc-button[basic]',
  templateUrl: './button.component.html',
  styleUrls: ['./button.component.scss'],
})
export class ButtonComponent {
  @Input('id') id: string = 'kc-button';
  @Input('name') name: string = '';
  @Input('classes') classes: string = '';
  @Input('disabled') disabled: boolean = false;
  @Input('color') color: string = 'primary';
  @Input('icon') icon: string = '';
  @Input('iconPosition') iconPosition: string = 'left';

  @Output('click') handlerClick: EventEmitter<MouseEvent> =
    new EventEmitter<MouseEvent>();

  constructor() {}

  onClick(event: MouseEvent) {
    this.handlerClick.emit(event);
  }
}

import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';

@Component({
  selector: 'kc-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss'],
})
export class CardComponent implements OnInit {
  @ViewChild('cardFooter', { read: ElementRef, static: true })
  cardFooter: ElementRef = {} as ElementRef;
  @ViewChild('cardHeader', { read: ElementRef, static: true })
  cardHeader: ElementRef = {} as ElementRef;

  hasFooter = false;
  hasHeader = false;

  constructor() {}

  ngOnInit() {
    this.hasFooter = !!this.cardFooter?.nativeElement.innerHTML;
    this.hasHeader = !!this.cardHeader?.nativeElement.innerHTML;
  }

  // ngAfterContentInit() {
  //   this.hasFooter = !!this.cardFooter?.nativeElement.innerHTML;
